import React, { useState, useRef, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import CustomModal from "./Modal";
import "./input.css";

export function Calendario() {
  const [state, setState] = useState({});

  const [title, setTitle] = useState("");
  const [color, setColor] = useState("");
  const [start, setStart] = useState(new Date());
  const [end, setEnd] = useState(new Date());
  const [allDay, setAllDay] = useState(false);

  const [eventi, setEventi] = useState([])

  const [modal, setModal] = useState(false);
  const calendarRef = useRef(null);


  //FETCH EVENTI METHOD GET
  function fetchEventi() {
      fetch("http://localhost:1337/api/eventi")
      .then((res) => res.json())
      .then(res => setEventi(res.data))
  }
  
  //RENDER EVENTI ALL'APERTURA
  useEffect(() => {
    fetchEventi();
  }, [])


  //CHIUSURA E RESET MODALE
  function handleClose() {
    setTitle("");
    setColor("");
    setStart(new Date());
    setEnd(new Date());
    setAllDay(false)
    setState({});
    setModal(false);
  }

  //SELEZIONA DATA CREAZIONE EVENTO
  function handleDateSelect(selectInfo) {
    if (selectInfo.view.calendar) {
      console.log(selectInfo.view.calendar);
      selectInfo.view.calendar.unselect();
      // setState({
      //   selectInfo,
      //   state: "creare",
      // });
      setStart(selectInfo.startStr)
      setEnd(selectInfo.endStr)
      setModal(true);
      setAllDay(selectInfo.allDay)
      console.log(selectInfo);
    }
  }

  //RENDERIZZAZIONE EVENTI
  function renderEventContent(eventInfo) {
    return (
      <div
        style={{
          overflow: "hidden",
          textOverflow: "ellipsis",
          background: `${eventInfo.event.color}`,
        }}
      >
        <i>{eventInfo.event.title}</i>
      </div>
    );
  }

  //AGGIORNAMENTO EVENTI
  function handleEventClick(clickInfo) {
    console.log(color);
    setState({ clickInfo, state: "Aggiorna" });
    setTitle(clickInfo.event.title);
    setColor(clickInfo.event.backgroundColor)
    setStart(clickInfo.event.start);
    setEnd(clickInfo.event.end);
    setAllDay(clickInfo.event.allDay)
    setModal(true);
    console.log(clickInfo);
  }

  //MODIFICA EVENTO + FETCH METHOD PUT
  function handleEdit(e) {
    e.preventDefault();
    fetch(`http://localhost:1337/api/eventi/${state.clickInfo.event.id}`, {
        method: "PUT",
        headers: {
          Accept: "application/json",
          "Content-type": "application/json"
        },
        body: JSON.stringify({
          data: {
            titolo: title,
            colore: color,
            oraInizio: start,
            oraFine: end,
            tuttoGiorno: allDay
          }
        })
      })
      .then(fetchEventi)

    handleClose();
  }

  //INVIO EVENTI + METHOD POST
  function handleSubmit(e) {
    e.preventDefault();

    if (!title) {
      return;
    }

      fetch("http://localhost:1337/api/eventi", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-type": "application/json"
        },
        body: JSON.stringify({
          data: {
            titolo: title,
            colore: color,
            oraInizio: start,
            oraFine: end,
            tuttoGiorno: allDay
          }
        })
      })
      .then(fetchEventi)
      .then(handleClose)
  }

  //FETCH ELIMINA EVENTI
  function handleDelete() {
    fetch(`http://localhost:1337/api/eventi/${state.clickInfo.event.id}`, {method: "DELETE"})
    .then (fetchEventi)
    handleClose();
  }

  useEffect(() => {
    console.log(state);
  })
  //RIDIMENSIONAMENTO EVENTI (FETCH METHOD PUT)
  function handleEventResize(checkInfo) {
    setState({ checkInfo, state: "resize" });

    

    // fetch(`http://localhost:1337/api/eventi/${state.checkInfo.event.id}`, {
    //     method: "PUT",
    //     headers: {
    //       Accept: "application/json",
    //       "Content-type": "application/json"
    //     },
    //     body: JSON.stringify({
    //       data: {
    //         titolo: title,
    //         colore: color,
    //         oraInizio: start,
    //         oraFine: end,
    //         tuttoGiorno: allDay
    //       }
    //     })
    //   })
    //   .then(fetchEventi)

    handleClose();
  }

  //CAMBIO VALORE COLOR TRAMITE I TRE INPUT
  function handleChange(e) {
    setColor(e.target.value)
  }

  return (
    <div className="sd:container bg glass-component box-bg">
      <CustomModal
        title={
          state.state === "Aggiorna" ? (
            "Aggiorna Evento"
          ) : (
            <p className="text-[#5d5d5d] mb-3">Aggiungi Evento</p>
          )
        }
        open={modal}
        onClose={handleClose}
        onCancel={handleClose}
        onSubmit={state.clickInfo ? handleEdit : handleSubmit}
        submitText={state.clickInfo ? "Aggiorna" : "Salva"}
        onDelete={state.clickInfo && handleDelete}
        deleteText="Cancella"
      >
        <form onSubmit={state.clickInfo ? handleEdit : handleSubmit}>
          <p className="text-[#5d5d5d] mb-3">Titolo Evento</p>
          <input
            className="text-[#5d5d5d] rounded-md indent-1"
            type="text"
            name="title"
            placeholder="inserisci evento"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <br />
          <input
            className="accent-red-700 mt-2 mr-1"
            type="checkbox"
            value={"#FF0000"}
            onChange={(e) => handleChange(e)}
            name="rosso"
          />
          <span className="mr-1">Rosso</span>
          <input
            className="accent-green-700 mt-2 mr-1"
            type="checkbox"
            value={"#00ff00"}
            onChange={(e) => handleChange(e)}
            name="verde"
          />
          <span className="mr-1">Verde</span>
          <input
            className="accent-yellow-500 mt-2 mr-1"
            type="checkbox"
            value={"#ffff00"}
            onChange={(e) => handleChange(e)}
            name="giallo"
          />
          <span>Giallo</span>
        </form>
      </CustomModal>

      <FullCalendar
        ref={calendarRef}
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        headerToolbar={{
          left: "prev,today,next",
          center: "title",
          right: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
        buttonText={{
          today: "corrente",
          month: "mese",
          week: "settimana",
          day: "giorno",
          list: "list",
        }}
        initialView="dayGridMonth"
        editable={true}
        selectable={true}
        selectMirror={true}
        dayMaxEvents={true}
        locale="it"
        events={
          eventi?.map(evento => (
            {
              id: evento.id,
              title: evento.attributes.titolo,
              color: evento.attributes.colore,
              start: evento.attributes.oraInizio,
              end: evento.attributes.oraFine,
              allDay: evento.attributes.tuttoGiorno
          }
          ))
        } // alternatively, use the `events` setting to fetch from a feed
        select={handleDateSelect}
        eventContent={renderEventContent} // RENDERIZZAZIONE E PERSONALIZZAZIONE EVENTI
        eventClick={handleEventClick}
        eventResize={handleEventResize}
      />
    </div>
  );
}
