import React, { useState, useRef } from "react";
// import "@fullcalendar/common";
import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import { nanoid } from "nanoid";
import CustomModal from "./Modal";
import "./input.css";
//import axios from "axios";

let todayStr = new Date().toISOString().replace(/T.*$/, "");

export function Calendario() {
  const [state, setState] = useState({});
  const [currentEvents, setCurrentEvents] = useState([]);
  const [check, setCheck] = useState(false);
  const [color, setColor] = useState("");
  const [modal, setModal] = useState(false);
  const [title, setTitle] = useState("");
  const [start, setStart] = useState(new Date());
  const [end, setEnd] = useState(new Date());
  const calendarRef = useRef(null);

  const handleCloseModal = () => {
    handleClose();
    setModal(false);
  };

  function handleDateSelect(selectInfo) {
    if (selectInfo.view.calendar) {
      selectInfo.view.calendar.unselect();
      setState({
        selectInfo,
        state: "creare",
      });

      // Open modal create
      console.log("open modal create");
      setStart(selectInfo.start);
      setEnd(selectInfo.end);

      setModal(true);
    }
  }

  function renderEventContent(eventInfo) {
    return (
      <div
        style={{
          overflow: "hidden",
          textOverflow: "ellipsis",
          background: `${eventInfo.event.color}`,
        }}
      >
        <i>{eventInfo.event.title}</i>
      </div>
    );
  }

  function handleEventClick(clickInfo) {
    setState({ clickInfo, state: "Aggiorna" });
    // set detail
    setTitle(clickInfo.event.title);
    setStart(clickInfo.event.start);
    setEnd(clickInfo.event.end);
    setColor(clickInfo.event.backgroundColor)
    setModal(true);
  }

  function handleEvents(events) {
    setCurrentEvents(events);
  }

  function handleEdit(e) {
    e.preventDefault();

    // state.clickInfo.event.setStart(start);
    // state.clickInfo.event.setEnd(end);
    console.log(state.clickInfo.event);

    // state.clickInfo.event.setColor({background: color}) 
    
    state.clickInfo.event.mutate({
      standardProps: {
        title,
        backgroundColor: color
      },
    });

    setColor('');
    handleClose();
  }

  function handleSubmit(e) {
    e.preventDefault();

    if (!title) {
      return;
    }

    const newEvent = {
      id: nanoid(),
      title,
      color: color,
      start: state.selectInfo?.startStr || start.toISOString(),
      end: state.selectInfo?.endStr || end.toISOString(),
      allDay: state.selectInfo?.allDay || false,
    };

    let calendarApi = calendarRef.current.getApi();

    calendarApi.addEvent(newEvent);
    setColor("");
    handleClose();
    // axios
    //   .post("http://localhost:1337/api/eventis", {
    //     data: {
    //       Title: title,
    //     }
    //   })
    //   .then((res) => {
    //     console.log(res);
    //   });
  }

  function handleDelete() {
    state.clickInfo.event.remove();
    handleClose();
  }

  function handleClose() {
    setTitle("");
    setStart(new Date());
    setEnd(new Date());
    setState({});
    setModal(false);
  }

  function handleChange(e) {
    if (color === "" && e.target.name === "rosso") {
      setColor("#FF0000");
    } else if (color === "" && e.target.name === "verde") {
      return setColor("#00ff00");
    } else if (color === "" && e.target.name === "giallo") {
      setColor("#ffff00");
    } else {
      setColor("");
    }
  }

  return (
    <div className="sd:container bg glass-component box-bg">
      <CustomModal
        title={
          state.state === "Aggiorna" ? (
            "Aggiorna Evento"
          ) : (
            <p className="text-[#5d5d5d] mb-3">Aggiungi Evento</p>
          )
        }
        open={modal}
        onClose={handleCloseModal}
        onCancel={handleCloseModal}
        onSubmit={state.clickInfo ? handleEdit : handleSubmit}
        submitText={state.clickInfo ? "Aggiorna" : "Salva"}
        onDelete={state.clickInfo && handleDelete}
        deleteText="Cancella"
      >
        <form onSubmit={state.clickInfo ? handleEdit : handleSubmit}>
          <p className="text-[#5d5d5d] mb-3">Titolo Evento</p>
          <input
            className="text-[#5d5d5d] rounded-md indent-1"
            type="text"
            name="title"
            placeholder="inserisci evento"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <br />
          <input
            className="accent-red-700 mt-2 mr-1"
            type="checkbox"
            onChange={(e) => handleChange(e)}
            name="rosso"
          />
          <span className="mr-1">Rosso</span>
          <input
            className="accent-green-700 mt-2 mr-1"
            type="checkbox"
            onChange={(e) => handleChange(e)}
            name="verde"
          />
          <span className="mr-1">Verde</span>
          <input
            className="accent-yellow-500 mt-2 mr-1"
            type="checkbox"
            onChange={(e) => handleChange(e)}
            name="giallo"
          />
          <span>Giallo</span>
        </form>
      </CustomModal>

      <FullCalendar
        ref={calendarRef}
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        headerToolbar={{
          left: "prev,today,next",
          center: "title",
          right: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
        buttonText={{
          today: "corrente",
          month: "mese",
          week: "settimana",
          day: "giorno",
          list: "list",
        }}
        initialView="dayGridMonth"
        editable={true}
        selectable={true}
        selectMirror={true}
        dayMaxEvents={true}
        locale="it"
        initialEvents={[
          {
            id: nanoid(),
            title: "Graduation Day",
            start: todayStr,
          },
        ]} // alternatively, use the `events` setting to fetch from a feed
        select={handleDateSelect}
        eventContent={renderEventContent} // custom render function
        eventClick={handleEventClick}
        //dateClick={handleDateClick}
      />
    </div>
  );
}
